import React, { useState } from 'react';
import './Header.css';
import logo from '../../assets/logo.svg';
import { Button } from '@material-ui/core';
import Modal from '@material-ui/core/Modal';

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();
    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const Header = function () {
    //let accessToken = useState(false,setAccessToken);
    const [accessToken, setAccessToken] = useState(false)
    const [releasedMovieClicked, setReleasedMovieClicked] = useState(true)
    const handleClose = () => {
        setAccessToken(false);
    };
    const [modalStyle] = React.useState(getModalStyle);
    var loginFunction = ()=>{
        return (
            <div>
            <Button variant="contained" className='login' disabled={accessToken} onClick={()=>{
                setAccessToken(true)
            }}>Login</Button>
            <Modal
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                open={accessToken}
                onClose={handleClose}
            >
                <div style={modalStyle} className='modalData'>
                    <h2>Simple React Modal</h2>
                    <p>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi accumsan odio enim, non pharetra est ultrices et.
                    </p>
                </div>
            </Modal>
            </div>
        )
    }
    var logoutFunction = ()=>{
        return (
            <Button variant="contained" className='logout' disabled={!accessToken} onClick={()=>{
                setAccessToken(false)
            }}>Logout</Button>
        )
    }
    return (
        <div className="header">
            <span>
            <img src={logo} className='logo'></img>
            </span>
            {!accessToken?loginFunction():logoutFunction()}
            <Button variant="contained" className='bookShow' color='primary' disabled={!releasedMovieClicked} onClick={()=>{
                setAccessToken(false)
            }}>Book Show</Button>
        </div>
    )
}

export default Header;
