import React, { Component, useState } from 'react';
import './Home.css';
import Header from '../../common/header/Header';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const styles = theme => ({
    root: {
      flexGrow: 1,
      backgroundColor: theme.palette.background.paper
    },
    upcomingMoviesHeading: {
      textAlign: 'center',
      background: '#ff9999',
      padding: '8px',
      fontSize: '1rem'
    },
    gridListUpcomingMovies: {
      flexWrap: 'nowrap',
      transform: 'translateZ(0)',
      width: '100%'
    },
    gridListMain: {

      transform: 'translateZ(0)',
      cursor: 'pointer'
    },
    formControl: {
      margin: theme.spacing.unit,
      minWidth: 240,
    },
    title: {
      color: theme.palette.primary.light,
    }
  });

const Home = function () {
    //let accessToken = useState(false,setAccessToken);
    const [accessToken, setAccessToken] = useState(false)
    const [releasedMovieClicked, setReleasedMovieClicked] = useState(true)
    const [homepageState,setHomePageState] = useState({
            movieName: "",
            upcomingMovies: [{}],
            releasedMovies: [{}],
            genresList: [{}],
            artistsList: [{}],
            genres: [],
            artists: [],
            releaseDateStart: "",
            releaseDateEnd: ""
          }
    )



    var color = '#ff9999'
    const movieHeading = function(){
        return (
            <div className='movieHeading'>
                Upcoming Movies
            </div>
        );
    }
    var gridTileStyle= {
        position: 'relative',
        float: 'left',
        width: '100%',
        minHeight: '400px',
        minWidth: '664px',
        overflow: 'hidden',
        height: '100% !important'
  }
    return (
        <div className='homepage'>
            <Header />
            <div className='movieHeading'>
                Upcoming Movies
            </div>
            <GridList cols={5} className={classes.gridListUpcomingMovies}>
               {homepageState.upcomingMovies.map(movie => (
                <GridListTile key={"upcoming" + movie.id}>
                   <img src={movie.poster_url} alt={movie.title} />
                   <GridListTileBar title={movie.title} />
                </GridListTile>
               ))}
            </GridList>
            <div className="flex-container">
          <div className="left">
            <GridList cellHeight={350} cols={4} className={classes.gridListMain}>
              {this.state.releasedMovies.map(movie => (
                <GridListTile onClick={() => this.movieClickHandler(movie.id)} className="marginMovie" key={"grid" + movie.id}>
                  <img src={movie.poster_url} alt={movie.title} />
                  <GridListTileBar
                    title={movie.title}
                    subtitle={<span>Release Date: {new Date(movie.release_date).toDateString()}</span>}
                  />
                </GridListTile>
              ))}
            </GridList>
          </div>
          <div className="right">
            <Card>
              <CardContent>
                <FormControl className={classes.formControl}>
                  <Typography className={classes.title} color="textSecondary">
                    FIND MOVIES BY:
                  </Typography>
                </FormControl>
                <FormControl className={classes.formControl}>
                  <InputLabel htmlFor="movieName"> Movie Name </InputLabel>
                  <Input id="movieName" onChange={this.movieNameChangeHandler} />
                </FormControl>
                <FormControl className={classes.formControl}>
                  <InputLabel htmlFor="select-multiple-checkbox"> Genre</InputLabel>
                  <Select
                    multiple
                    input={<Input id="select-multiple-checkbox" />}
                    renderValue={selected => selected.join(',')}
                    value={this.state.genres}
                    onChange={this.genreSelectHandler}>
                    <MenuItem value="0">None
                   </MenuItem>
                    {this.state.genresList.map(genre => (
                      <MenuItem key={"genre" + genre.id} value={genre.genre}>
                        <Checkbox checked={this.state.genres.indexOf(genre.genre) > - 1} />
                        <ListItemText primary={genre.genre} />
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl className={classes.formControl}>
                  <InputLabel htmlFor="select-multiple-checkbox"> Artists</InputLabel>
                  <Select
                    multiple
                    input={<Input id="select-multiple-checkbox" />}
                    renderValue={selected => selected.join(',')}
                    value={this.state.artists}
                    onChange={this.artistsSelectHandler}>
                    <MenuItem value="0">None
                   </MenuItem>
                    {this.state.artistsList.map(artist => (
                      <MenuItem key={"artist" + artist.id} value={artist.first_name + " " + artist.last_name}>
                        <Checkbox checked={this.state.artists.indexOf(artist.first_name + " " + artist.last_name) > - 1} />
                        <ListItemText primary={artist.first_name + " " + artist.last_name} />
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <FormControl className={classes.formControl}>
                  <TextField
                    id="releaseDateStart"
                    label="Release Date Start"
                    type="date"
                    defaultValue=""
                    InputLabelProps={{ shrink: true }}
                    onChange={this.releaseDateStartHandler}
                  />
                </FormControl>
                <FormControl className={classes.formControl}>
                  <TextField
                    id="releaseDateEnd"
                    label="Release Date End"
                    type="date"
                    defaultValue=""
                    InputLabelProps={{ shrink: true }}
                    onChange={this.releaseDateEndHandler}
                  />
                </FormControl><br /><br />
                <FormControl className={classes.formControl}>
                  <Button onClick={() => this.filterApplyHandler()} variant="contained" color="primary">
                    APPLY
                    </Button>
                </FormControl>

              </CardContent>
            </Card>

            </div>
          </div>
        </div>
    )
}

export default Home;
