import React, { Component, useEffect, useState } from 'react';
import './Home.css';
import Header from '../../common/header/Header';
import { withStyles } from '@material-ui/core/styles';
import ImageList from '@material-ui/core/ImageList';
import ImageListItem from '@material-ui/core/ImageListItem';
import ImageListItemBar from '@material-ui/core/ImageListItemBar';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const styles = theme => ({
    root: {
      flexGrow: 1,
      backgroundColor: theme.palette.background.paper
    },
    upcomingMoviesHeading: {
      textAlign: 'center',
      background: '#ff9999',
      padding: '8px',
      fontSize: '1rem'
    },
    gridListUpcomingMovies: {
      flexWrap: 'nowrap',
      transform: 'translateZ(0)',
      width: '100%'
    },
    gridListMain: {

      transform: 'translateZ(0)',
      cursor: 'pointer'
    },
    formControl: {
      margin: theme.spacing(240),
      minWidth: 240,
    },
    title: {
      color: theme.palette.primary.light,
    }
  });

const Home = function (props) {
    //let accessToken = useState(false,setAccessToken);
    const [accessToken, setAccessToken] = useState(false)
    const [releasedMovieClicked, setReleasedMovieClicked] = useState(true)
    const publishMovies = [{}]
    const releaseMovies = [{}]
    const geners = []
    const artists = []
    useEffect(()=>{
        let fetchMovies = fetch("http://localhost:8085/api/v1/movies?status=PUBLISHED");
        fetchMovies.then(res => res.json()).then(d => {
            publishMovies.push(d)
        })

        let releasedMovies = fetch("http://localhost:8085/api/v1/movies?status=RELEASED");
        releasedMovies.then(res => res.json()).then(d => {
            releaseMovies.push(d)
        })

        let genresList = fetch("http://localhost:8085/api/v1/genres");
        genresList.then(res => res.json()).then(d => {
            geners.push(d)
        })

        let artistList = fetch("http://localhost:8085/api/v1/artists");
        artistList.then(res => res.json()).then(d => {
            artists.push(d)
        })
    });

    const [homepageState,setHomePageState] = useState({
        movieName: "",
        upcomingMovies: publishMovies,
        releasedMovies: [{}],
        genresList: [{}],
        artistsList: [{}],
        genres: [],
        artists: [],
        releaseDateStart: "",
        releaseDateEnd: ""
      }
)

    const movieNameChangeHandler = event => {
        setHomePageState({ movieName: event.target.value });
      }

    const  genreSelectHandler = event => {
        setHomePageState({ genres: event.target.value });
      }

    const  artistsSelectHandler = event => {
        setHomePageState({ artists: event.target.value });
      }

    const releaseDateStartHandler = event => {
        setHomePageState({ releaseDateStart: event.target.value });
      }

    const  releaseDateEndHandler = event => {
        setHomePageState({ releaseDateEnd: event.target.value });
      }

    const  movieClickHandler = (movieId) => {
        props.history.push('/movie/' + movieId);
      }

    const { classes } = props;
    console.log(homepageState)
    return (
        <div className='homepage'>
            <Header />
            <div className='movieHeading'>
                Upcoming Movies
            </div>
            <ImageList cols={5} className={classes.gridListUpcomingMovies}>
               {homepageState.upcomingMovies.map(movie => (
                <ImageListItem key={"upcoming" + movie.id}>
                   <img src={movie.poster_url} alt={movie.title} />
                   <ImageListItemBar title={movie.title} />
                </ImageListItem>
               ))}
            </ImageList>
            <div className="flex-container">
               <div className="left">
                  <ImageList rowHeight={350} cols={4} className={classes.gridListMain}>
                   {homepageState.releasedMovies.map(movie => (
                      <ImageListItem onClick={() => movieClickHandler(movie.id)} className="marginMovie" key={"grid" + movie.id}>
                         <img src={movie.poster_url} alt={movie.title} />
                         <ImageListItemBar
                            title={movie.title}
                            subtitle={<span>Release Date: {new Date(movie.release_date).toDateString()}</span>}
                         />
                      </ImageListItem>
                   ))}
                  </ImageList>
                </div>
                <div className="right">
                  <Card>
                    <CardContent>
                      <FormControl className={classes.formControl}>
                         <Typography className={classes.title} color="textSecondary">
                            FIND MOVIES BY:
                         </Typography>
                      </FormControl>
                      <FormControl className={classes.formControl}>
                         <InputLabel htmlFor="movieName"> Movie Name </InputLabel>
                           <Input id="movieName" onChange={movieNameChangeHandler} />
                      </FormControl>
                      <FormControl className={classes.formControl}>
                         <InputLabel htmlFor="select-multiple-checkbox"> Genre</InputLabel>
                           <Select
                             multiple
                             input={<Input id="select-multiple-checkbox" />}
                             renderValue={selected => selected.join(',')}
                             value={homepageState.genres}
                             onChange={genreSelectHandler}>
                           <MenuItem value="0">None
                           </MenuItem>
                          {homepageState.genresList.map(genre => (
                            <MenuItem key={"genre" + genre.id} value={genre.genre}>
                             <Checkbox checked={homepageState.genres.indexOf(genre.genre) > - 1} />
                             <ListItemText primary={genre.genre} />
                            </MenuItem>
                          ))}
                        </Select>
                      </FormControl>
                      <FormControl className={classes.formControl}>
                         <InputLabel htmlFor="select-multiple-checkbox"> Artists</InputLabel>
                         <Select
                           multiple
                           input={<Input id="select-multiple-checkbox" />}
                           renderValue={selected => selected.join(',')}
                           value={homepageState.artists}
                           onChange={artistsSelectHandler}>
                           <MenuItem value="0">None
                           </MenuItem>
                           {homepageState.artistsList.map(artist => (
                           <MenuItem key={"artist" + artist.id} value={artist.first_name + " " + artist.last_name}>
                             <Checkbox checked={homepageState.artists.indexOf(artist.first_name + " " + artist.last_name) > - 1} />
                             <ListItemText primary={artist.first_name + " " + artist.last_name} />
                           </MenuItem>
                           ))}
                          </Select>
                        </FormControl>
                        <FormControl className={classes.formControl}>
                        <TextField
                          id="releaseDateStart"
                          label="Release Date Start"
                          type="date"
                          defaultValue=""
                          InputLabelProps={{ shrink: true }}
                          onChange={releaseDateStartHandler}
                        />
                        </FormControl>
                        <FormControl className={classes.formControl}>
                           <TextField
                             id="releaseDateEnd"
                             label="Release Date End"
                             type="date"
                             defaultValue=""
                             InputLabelProps={{ shrink: true }}
                             onChange={releaseDateEndHandler}
                           />
                        </FormControl><br /><br />
                        <FormControl className={classes.formControl}>
                          <Button onClick={() => filterApplyHandler()} variant="contained" color="primary">
                            APPLY
                          </Button>
                        </FormControl>

                 </CardContent>
              </Card>

            </div>
          </div>
        </div>
    )
}

export default withStyles(styles)(Home);
